package math;

public class Exponent {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.exp(1)); // return 2.71828
		System.out.println(Math.log(Math.E)); // return 1.0
		System.out.println(Math.log10(10)); // return 1.0
		System.out.println(Math.pow(2, 3)); // return 8.0
		System.out.println(Math.pow(3, 2)); // return 9.0
		System.out.println(Math.pow(4.5,  2.5)); // return 22.91765
		System.out.println(Math.sqrt(4));  // return 2.0
		System.out.println(Math.sqrt(10.5)); // return 3.2
	}

}
