package math;

public class MaxMinAbs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.max(2,  3)); // return 3
		System.out.println(Math.max(2.5,  3)); // return 3.0
		System.out.println(Math.min(2.5,  4.6)); // return 2.5
		System.out.println(Math.abs(-2)); // return 2
		System.out.println(Math.abs(-2.1));  // return 2.1
	}
}
