package math;

public class Rounding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.ceil(2.1)); // return 3.0
		System.out.println(Math.ceil(2.0)); // return 2.0
		System.out.println(Math.ceil(-2.0)); // return -2.0
		System.out.println(Math.ceil(-2.1)); // return -2.0
		System.out.println(Math.floor(2.1)); // return 2.0
		System.out.println(Math.floor(2.0)); // return 2.0
		System.out.println(Math.floor(-2.0)); // return -2.0
		System.out.println(Math.floor(-2.1)); // return -3.0
		System.out.println(Math.rint(-2.1)); // return 2.0
		System.out.println(Math.rint(2.5)); // return 2.0
		System.out.println(Math.rint(4.5)); // return 4.0
		System.out.println(Math.rint(-2.5)); // return -2.0
		System.out.println(Math.round(2.0)); // return 2
		System.out.println(Math.round(-2.0f)); // return -3
		System.out.println(Math.round(-2.6)); // return -3
		System.out.println(Math.round(-2.4)); // return -2.0
		
				
	}

}
