package selection;

public class TestIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 60;
		
		if (score >= 60)
			System.out.println("D");
		else if (score >= 70)
			System.out.println("C");	
		else if (score >= 80)
			System.out.println("B");	
		else if (score >= 90)
			System.out.println("A");		
	}

}
